mapper.insert:
INSERT INTO comment
SET comment_contents = #{commentContents}
, writer_id = #{writerId}
, board_id = #{boardId}

mapper.update:
UPDATE comment
SET comment_contents = #{commentContents}
WHERE comment_id = #{commentId}
AND writer_id = #{writerId}

mapper.delete:
DELETE FROM comment
WHERE comment_id = #{commentId}
AND writer_id = #{writerId}
